import React, { useState, useContext, useEffect } from "react";
import { Table } from 'react-bootstrap';
import { useNavigate} from "react-router-dom";
import AppContext from "../AppContext.js";


function MyAccount({ setIsLoggedIn }) {
  const navigate = useNavigate();
  const [userItems, setUserItems] = useState([]);
  const { userList, currentUser, setCurrentUser } = useContext(AppContext);

  const handleLogout = () => {
    setIsLoggedIn(false);
    navigate("/");
  };

  useEffect(() => {
    fetch(`http://localhost:3001/user-items/${currentUser.id}`)
      .then((response) => response.json())
      .then((data) => setUserItems(data))
      .catch((error) => console.error("Error fetching user data:", error));
  }, []);

  if (userItems.length > 0 && userList.length > 0) {
    return (
      <>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Quantity</th>
              <th>User ID</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {userItems.map((item, index) => (
              <tr key={index} >
                <td>{item.item_name}</td>
                <td>{item.quantity}</td>
                <td>{item.user_id}</td>
                <td>{item.description}</td>
                <td onClick={()=>navigate(`/user-item-details/${item.id}`)}><button>Edit</button></td>
              </tr>
            ))}
          </tbody>
        </Table>
      </>
    );
  }

  return (<p>Loading...</p>);
}

export default MyAccount;
