import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link, Outlet, useNavigate } from "react-router-dom";
import Inventory from "./components/Inventory";
import ItemDetails from "./components/ItemDetails";
import MyItems from "./components/MyItems";
import Login from "./components/Login";
import AppContext from "./AppContext.js";

function App() {
  const [itemList, setItemList] = useState([]);
  const [userList, setUserList] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const provided = {
    itemList,
    setItemList,
    userList,
    setUserList,
    currentUser,
    setCurrentUser,
    isLoggedIn,
    setIsLoggedIn,
  };

  return (
    <Router>
      <AppContext.Provider value={provided}>
        <Link to="/">Home</Link>
        {isLoggedIn ? (
          <React.Fragment>
            <button onClick={() => setIsLoggedIn(false)}>Logout</button>
            <Link to="/my-items">My Account</Link>
          </React.Fragment>
        ) : (
          <Link to="/login">Login</Link>
        )}

        <Routes>
          <Route path="/" element={<Outlet />}>
            <Route index element={isLoggedIn ? <MyItems /> : <Inventory />} />
            <Route path="item/:id" element={<ItemDetails />} />
          </Route>
          <Route path="/login" element={<Login />} />
        </Routes>
      </AppContext.Provider>
    </Router>
  );
}

export default App;
