import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import AppContext from "../AppContext.js";

function LoginPage({ setIsLoggedIn, closeModal }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState(false); // State variable for login error

  const { userList, setCurrentUser } = useContext(AppContext);
  const navigate = useNavigate();

  const handleLogin = () => {
    // Find the user with the matching username and password
    const user = userList.find(
      (user) => user.username === username && user.password === password
    );

    if (user) {
      setIsLoggedIn(true);
      closeModal(); // Close the modal after successful login
      navigate("/myaccount");
    } else {
      // If user not found, show the login error message and reset the fields
      setLoginError(true);
      setUsername("");
      setPassword("");
    }
  };

  return (
    <div style={{ background: "#fff", padding: "20px", borderRadius: "5px" }}>
      <h2>Login</h2>
      {loginError && <p style={{ color: "red" }}>Username or Password is incorrect</p>}
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
      <button onClick={closeModal}>Cancel</button>
    </div>
  );
}

export default LoginPage;
