import React, { useState, useContext, useNavigate } from "react";
import AppContext from "../AppContext.js";

function LoginPage({ setIsLoggedIn, closeModal }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const { userList, setCurrentUser } = useContext(AppContext);

const navigate = useNavigate();

  const handleLogin = () => {
    // Find the user with the matching username and password
    const user = userList.find(
      (user) => user.username === username && user.password === password
    );
    setCurrentUser(user);

    if (user) {
      setIsLoggedIn(true);
      closeModal(); // Close the modal after successful login
      navigate('/myaccount')
    }
  };

  return (
    <div style={{ background: "#fff", padding: "20px", borderRadius: "5px" }}>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
      <button onClick={closeModal}>Cancel</button>
    </div>
  );
}

export default LoginPage;
