import { useState, useEffect, useContext } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from "react-router-dom";
import Inventory from "./components/Inventory";
import ItemDetails from "./components/ItemDetails";
import MyItems from "./components/MyItems";
import LoginPage from "./components/LoginPage";
import MyAccount from "./components/MyAccount";
import AppContext from "./AppContext.js";
import UserItemDetails from "./components/UserItemDetails";

function App() {
  const [itemList, setItemList] = useState([]);
  const [userList, setUserList] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState();


  const provided = {
    itemList,
    setItemList,
    userList,
    setUserList,
    isLoggedIn,
    setIsLoggedIn,
    currentUser,
    setCurrentUser,
  };

  // Use the useLocation hook to get the current location
  const location = useLocation();

  return (
    <AppContext.Provider value={provided}>
      <Router>
        <header>
          {isLoggedIn ? (
            // Check if the current location is not '/myaccount' before rendering the button
            location.pathname !== '/myaccount' && (
              <Link to="/myaccount">
                <button>My Account</button>
              </Link>
            )
          ) : (
            <Link to="/login">
              <button>Login</button>
            </Link>
          )}
        </header>
        <Routes>
          <Route path="/" element={<Inventory />} />
          <Route path="item/:id" element={<ItemDetails />} />
          <Route
            path="/login"
            element={<LoginPage setIsLoggedIn={setIsLoggedIn} />}
          />
          <Route
            path="/myaccount"
            element={<MyAccount setIsLoggedIn={setIsLoggedIn} />}
          />
          <Route 
          path="/user-item-details/:id"
          element={<UserItemDetails />}
          />
        </Routes>
      </Router>
    </AppContext.Provider>
  );
}

export default App;
