import { useEffect, useContext, useState } from 'react';
import { Table } from 'react-bootstrap';
import { useNavigate, useParams } from 'react-router-dom';
import AppContext from '../AppContext.js';

export default function UserItemDetails() {
  const { itemList, setItemList, userList, setUserList } = useContext(AppContext);
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [editItem, setEditItem] = useState(null); // State to hold edited item data
  const navigate = useNavigate();

  useEffect(() => {
    if (itemList && itemList.length > 0) {
      const selectedItem = itemList.find((item) => item.id === Number(id));
      setItem(selectedItem);
      setEditItem(selectedItem); // Initialize the editItem state with the existing data
    }
  }, [id, itemList]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditItem((prevItem) => ({
      ...prevItem,
      [name]: value,
    }));
  };

  const handleUpdate = () => {
    // Perform your update logic here using the editItem state
    // For example, you can make a PUT request to update the item in the database

    // After successful update, you can update the itemList state in the context and navigate back
    const updatedItemList = itemList.map((item) =>
      item.id === editItem.id ? editItem : item
    );
    setItemList(updatedItemList);
    navigate("/myaccount");
  };

  const handleDelete = () => {
    // Perform your delete logic here
    // After successful delete, you can update the itemList state in the context and navigate back
    const updatedItemList = itemList.filter((item) => item.id !== Number(id));
    setItemList(updatedItemList);
    navigate("/myaccount");
  };

  if (item && userList) {
    return (
      <>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Quantity</th>
              <th>User ID</th>
              <th>Username</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>
                <input
                  type="text"
                  name="item_name"
                  value={editItem.item_name}
                  onChange={handleInputChange}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="quantity"
                  value={editItem.quantity}
                  onChange={handleInputChange}
                />
              </td>
              <td>{item.user_id}</td>
              <td>{userList[item.user_id - 1].username}</td>
              <td>
                <input
                  type="text"
                  name="description"
                  value={editItem.description}
                  onChange={handleInputChange}
                />
              </td>
            </tr>
          </tbody>
        </Table>
        <button onClick={handleUpdate}>Update Item</button>
        <button onClick={handleDelete}>Delete Item</button>
      </>
    );
  }

  return null;
}
