import React, { useState, useEffect, useContext } from 'react';
import { Table } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import AppContext from '../AppContext.js';
import EditUserItem from './EditUserItem';

function MyAccount({ setIsLoggedIn }) {
  const navigate = useNavigate();
  const [userItems, setUserItems] = useState([]);
  const { currentUser } = useContext(AppContext);

  useEffect(() => {
    fetch(`http://localhost:3001/user-items/${currentUser.id}`)
      .then((response) => response.json())
      .then((data) => setUserItems(data))
      .catch((error) => console.error('Error fetching user data:', error));
  }, [currentUser.id]);

  const handleUserDelete = () => {
    // Send DELETE request to delete the user
    fetch(`http://localhost:3001/users/${currentUser.id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (response.ok) {
          // User deleted successfully
          // Show a confirmation message
          window.alert('User deleted successfully.');
          // Redirect to the inventory page
          setIsLoggedIn(false);
          navigate('/');
        } else {
          throw new Error('Failed to delete user');
        }
      })
      .catch((error) => {
        console.error('Delete error:', error);
      });
  };

  if (userItems.length > 0) {
    return (
      <>
        <button onClick={handleUserDelete}>Delete User</button>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Quantity</th>
              <th>User ID</th>
              <th>Description</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {userItems.map((item) => (
              <EditUserItem key={item.id} item={item} setUserItems={setUserItems} />
            ))}
          </tbody>
        </Table>
      </>
    );
  }

  return <p>Loading...</p>;
}

export default MyAccount;
