import React, { useState, useContext, useEffect } from "react";
import { useNavigate} from "react-router-dom";
import AppContext from "../AppContext.js";


function MyAccount({ setIsLoggedIn }) {
  const navigate = useNavigate();
  const [userItems, setUserItems] = useState();
  const { userList, currentUser, setCurrentUser } = useContext(AppContext);
console.log(currentUser.id)
  const handleLogout = () => {
    setIsLoggedIn(false);
    navigate("/");
  };
  useEffect(() => {
    fetch(`http://localhost:3001/user-items/${currentUser.id}`)
      .then((response) => response.json())
      .then((data) => setUserItems(data))
      .catch((error) => console.error("Error fetching user data:", error));
  }, []);

  console.log(userItems)
  if (userItems.length > 0 && userList.length > 0) {
    return (
      <>
      <button onClick={handleLogout}>Logout</button>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Quantity</th>
              <th>User ID</th>
              <th>Username</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {userItems.map((item, index) => (
              <tr key={index} onClick={() => handleItemClick(item.id)}>
                <td>{item.item_name}</td>
                <td>{item.quantity}</td>
                <td>{item.user_id}</td>
                <td>{userList[item.user_id - 1].username}</td>
                <td>{item.description.length > 100 ? `${item.description.slice(0, 97)}...` : item.description}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      </>
    );
  }

  return (<p>Loading...</p>);
}

export default MyAccount;
