import React, { useState, useContext } from "react";
import AppContext from "../AppContext.js";

function LoginPage({ setIsLoggedIn }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showModal, setShowModal] = useState(false);

  const { userList, currentUser, setCurrentUser } = useContext(AppContext);

  const handleLogin = () => {
    // Find the user with the matching username and password
    const user = userList.find((user) => user.username === username && user.password === password);
    setCurrentUser(user);

    if (user) {
      setIsLoggedIn(true);
      setShowModal(false); // Hide the modal after successful login
    }
  };

  const handleModalOpen = () => {
    setShowModal(true);
  };

  const handleModalClose = () => {
    setShowModal(false);
  };

  return (
    <div>
      <button onClick={handleModalOpen}>Login</button>

      {showModal && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0, 0, 0, 0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 9999,
          }}
        >
          <div style={{ background: "#fff", padding: "20px", borderRadius: "5px" }}>
            <h2>Login</h2>
            <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
            <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <button onClick={handleLogin}>Login</button>
            <button onClick={handleModalClose}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default LoginPage;
