import React, { useState } from "react";

function CreateUserModal({ onClose, onUserCreate, userList }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [usernameError, setUsernameError] = useState(false);

  const handleCreateUser = () => {
    // Check if the username is already taken
    const isUsernameTaken = userList.some((user) => user.username === username);

    if (isUsernameTaken) {
      // If username is taken, show the error message
      setUsernameError(true);
    } else {
      // If username is unique, create a new user object and pass it to onUserCreate function
      const newUser = {
        username,
        password,
        firstName,
        lastName,
      };
      onUserCreate(newUser);
    }
  };

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        background: "rgba(0, 0, 0, 0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 9999,
      }}
    >
      <div style={{ background: "#fff", padding: "20px", borderRadius: "5px" }}>
        <h2>Create User</h2>
        {usernameError && <p style={{ color: "red" }}>This username is already taken</p>}
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Last Name"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <button onClick={handleCreateUser}>Create</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
}

export default CreateUserModal;
