import React, { useState, useEffect, useContext } from "react";
import AppContext from "../AppContext.js";

function CreateUserModal({ onClose }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [usernameError, setUsernameError] = useState(false);
  const { userList, setUserList } = useContext(AppContext);

  const handleCreateUser = () => {
    // Check if the username is already taken in the current userList
    const isUsernameTaken = userList.some((user) => user.username === username);

    if (isUsernameTaken) {
      // If username is taken, show the error message
      setUsernameError(true);
    } else {
      // Create the new user object
      const newUser = {
        first_name: firstName,
        last_name: lastName,
        username,
        password,
      };
  console.log('new user')
  console.log(newUser)
      // Make a POST request to the API to create the new user
      fetch("http://localhost:3001/users", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newUser),
      })
        .then((response) => {
          // Check if the response status is OK (success)
          if (!response.ok) {
            throw new Error("Error inserting data into the database");
          }
          return response.json();
        })
        .then((data) => {
          // Check if the response data has the required properties for a valid user object
          if (
            data &&
            data.id &&
            data.first_name &&
            data.last_name &&
            data.username &&
            data.password
          ) {
            // Update the userList with the data from the server response
            setUserList((prevUserList) => [...prevUserList, data]);
            // Close the modal after successful user creation
            onClose();
          } else {
            // If the response data is not valid, throw an error
            throw new Error("Invalid response data");
          }
        })
        .catch((error) => {
          console.error("Error creating user:", error);
          // Show an error message or take other actions on error if needed
        });
        console.log(userList);
    }
  };




  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        background: "rgba(0, 0, 0, 0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 9999,
      }}
    >
      <div style={{ background: "#fff", padding: "20px", borderRadius: "5px" }}>
        <h2>Create User</h2>
        {usernameError && <p style={{ color: "red" }}>This username is already taken</p>}
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Last Name"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <button onClick={handleCreateUser}>Create</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
}

export default CreateUserModal;
