import React, { useState, useContext, useEffect } from 'react';
import AppContext from '../AppContext';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isFormValid, setIsFormValid] = useState(false);
  const { userList, setUserList, isLoggedIn, setIsLoggedIn } = useContext(AppContext);

  useEffect(() => {
  fetch('http://localhost:3001/users')
  .then((response) => response.json())
  .then((data) => {
    setUserList(data);
  })
  .catch((error) => {
    console.error('Fetch error:', error);
  });
}, []);

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
    setIsFormValid(e.target.value && password);
    console.log('username')
    console.log(e.target.value)
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setIsFormValid(username && e.target.value);
    console.log(e.target.value);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (isFormValid) {
        const currentUser = [username, password]
        console.log(currentUser);
        console.log(userList)
      const matchedUser = userList.find((user) => user.username === username && user.password === password);
      console.log(matchedUser)
      if (matchedUser) {
        setIsLoggedIn(true); 
        console.log(isLoggedIn)
      } else {
        console.log('Invalid username or password');
      }
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false); 
  };

  return (
    <div>
      {isLoggedIn ? (
        // If the user is logged in, show the "My Account" button
        <button onClick={handleLogout}>My Account</button>
      ) : (
        // If not logged in, show the login form
        <form onSubmit={handleLogin}>
          <div>
            <label>Username:</label>
            <input type="text" value={username} onChange={handleUsernameChange} />
          </div>
          <div>
            <label>Password:</label>
            <input type="password" value={password} onChange={handlePasswordChange} />
          </div>
          <button type="submit" disabled={!isFormValid}>
            Login
          </button>
        </form>
      )}
    </div>
  );
};

export default Login;
