import React, { useState, useEffect, useContext } from "react";
import AppContext from "../AppContext.js";

function CreateUserModal({ onClose }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [usernameError, setUsernameError] = useState(false);
  const { userList, setUserList } = useContext(AppContext);

  const handleCreateUser = () => {
    // Check if the username is already taken in the current userList
    const isUsernameTaken = userList.some((user) => user.username === username);

    if (isUsernameTaken) {
      // If username is taken, show the error message
      setUsernameError(true);
    } else {
      // If username is unique, create a new user object and add it to the userList
      const newUser = {
        first_name: firstName,
        last_name: lastName,
        username,
        password,
      };

      // Make a POST request to the API to create the new user
      fetch("http://localhost:3001/users", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newUser),
      })
        .then((response) => response.json())
        .then((data) => {
          // Update the userList with the data from the database
          // Fetch the updated userList from the API
          fetch("http://localhost:3001/users")
            .then((response) => response.json())
            .then((updatedUserList) => {
              setUserList(updatedUserList);
              // Close the modal after successful user creation
              onClose();
            })
            .catch((error) => {
              console.error("Error fetching updated user list:", error);
              // Show an error message or take other actions on error if needed
            });
        })
        .catch((error) => {
          console.error("Error creating user:", error);
          // Show an error message or take other actions on error if needed
        });
    }
  };

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        background: "rgba(0, 0, 0, 0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 9999,
      }}
    >
      <div style={{ background: "#fff", padding: "20px", borderRadius: "5px" }}>
        <h2>Create User</h2>
        {usernameError && <p style={{ color: "red" }}>This username is already taken</p>}
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Last Name"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <button onClick={handleCreateUser}>Create</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
}

export default CreateUserModal;
