import React, { useState, useEffect, useContext } from "react";
import { Table, Button } from 'react-bootstrap';
import { useNavigate } from "react-router-dom";
import AppContext from "../AppContext.js";
import CreateItemModal from "./CreateItemModal";

function MyAccount({ setIsLoggedIn }) {
  const navigate = useNavigate();
  const [userItems, setUserItems] = useState([]);
  const { userList, setUserList, currentUser, setCurrentUser } = useContext(AppContext);
  const [showCreateItemModal, setShowCreateItemModal] = useState(false);
  const [itemUpdated, setItemUpdated] = useState(false); // State variable to track item updates

  const handleUserDelete = () => {
    fetch(`http://localhost:3001/users/${currentUser.id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (response.ok) {
          const updatedUserList = userList.filter((user) => user.id !== currentUser.id);
          setUserList(updatedUserList);
          window.alert('User deleted successfully.');
          setIsLoggedIn(false);
          navigate("/");
        } else {
          throw new Error('Failed to delete user');
        }
      })
      .catch((error) => {
        console.error('Delete error:', error);
      });
  };

  useEffect(() => {
    fetch(`http://localhost:3001/user-items/${currentUser.id}`)
      .then((response) => response.json())
      .then((data) => setUserItems(data))
      .catch((error) => console.error("Error fetching user data:", error));
  }, [currentUser.id, itemUpdated]); // Add itemUpdated to the dependency array

  const handleItemUpdate = () => {
    // Call this function whenever an item is updated to trigger useEffect
    setItemUpdated(prev => !prev);
  };

  return (
    <>
      <Button onClick={handleUserDelete}>Delete User</Button>
      <Button onClick={() => setShowCreateItemModal(true)}>Create Item</Button>
      {userItems.length > 0 && userList.length > 0 ? (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Quantity</th>
              <th>User ID</th>
              <th>Description</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {userItems.map((item) => (
              <tr key={item.id}>
                <td>{item.item_name}</td>
                <td>{item.quantity}</td>
                <td>{item.user_id}</td>
                <td>{item.description}</td>
                <td>
                  <Button onClick={() => navigate(`/user-item-details/${item.id}`, { state: { handleItemUpdate } })}>
                    Edit
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      ) : (
        <p>Loading...</p>
      )}
      <CreateItemModal
        showModal={showCreateItemModal}
        onCloseModal={() => setShowCreateItemModal(false)}
        onItemCreate={handleItemUpdate} // Pass the function to update item
      />
    </>
  );
}

export default MyAccount;
