import { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import Inventory from "./components/Inventory";
import ItemDetails from "./components/ItemDetails";
import MyItems from "./components/MyItems";
import Login from "./components/Login";
import AppContext from "./AppContext.js";

function App() {
  const [itemList, setItemList] = useState([]);
  const [userList, setUserList] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const provided = {
    itemList,
    setItemList,
    userList,
    setUserList,
    currentUser, // Pass the currentUser state to the context provider
    setCurrentUser, // Pass the setCurrentUser function to the context provider
    isLoggedIn,
    setIsLoggedIn,
  };

  // Function to handle login
  const handleLogin = (credentials) => {
    // Perform the authentication logic here, e.g., checking the credentials against the userList state.
    const matchedUser = userList.find(
      (user) =>
        user.username === credentials.username && user.password === credentials.password
    );

    if (matchedUser) {
      setCurrentUser(matchedUser);
    } else {
      // Handle authentication failure (e.g., show an error message).
      console.log("Invalid username or password");
    }
  };

  return (
    <AppContext.Provider value={provided}>
      <Router>
        <button onClick={Navigate('/login')}>Log in</button>
        {/* <MyItems /> */}
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<Inventory />} />
          <Route path="item/:id" element={<ItemDetails />} />
        </Routes>
      </Router>
    </AppContext.Provider>
  );
}

export default App;
