import React, { useState } from 'react';

export default function EditUserItem({ item, isEditMode, onEditItem, setUserItems }) {
  const [editItem, setEditItem] = useState(item);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditItem((prevItem) => ({
      ...prevItem,
      [name]: value,
    }));
  };

  const handleUpdateItem = () => {
    // Send PUT request to update the item in the database
    fetch(`http://localhost:3001/user-items/${item.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(editItem),
    })
      .then((response) => response.json())
      .then((data) => {
        // Update the userItems state with the updated item
        setUserItems((prevUserItems) =>
          prevUserItems.map((userItem) =>
            userItem.id === item.id ? { ...userItem, ...data } : userItem
          )
        );
      })
      .catch((error) => {
        console.error('Update error:', error);
      })
      .finally(() => {
        // Exit edit mode after successful update
        onEditItem(null);
      });
  };

  const handleCancelEdit = () => {
    // Exit edit mode without saving changes
    onEditItem(null);
  };

  return (
    <tr>
      <td>{isEditMode ? <input type="text" name="item_name" value={editItem.item_name} onChange={handleInputChange} /> : item.item_name}</td>
      <td>{isEditMode ? <input type="text" name="quantity" value={editItem.quantity} onChange={handleInputChange} /> : item.quantity}</td>
      <td>{item.user_id}</td>
      <td>{isEditMode ? <input type="text" name="description" value={editItem.description} onChange={handleInputChange} /> : item.description}</td>
      <td>
        {!isEditMode ? (
          <button onClick={() => onEditItem(item.id)}>Edit</button>
        ) : (
          <>
            <button onClick={handleUpdateItem}>Save</button>
            <button onClick={handleCancelEdit}>Cancel</button>
          </>
        )}
      </td>
    </tr>
  );
}
