import { useState, useEffect, useContext } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from "react-router-dom";
import Inventory from "./components/Inventory";
import ItemDetails from "./components/ItemDetails";
import MyItems from "./components/MyItems";
import AppContext from "./AppContext.js";

function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const { userList } = useContext(AppContext);

  const handleLogin = () => {
    // Find the user with the matching username and password
    const user = userList.find((user) => user.username === username && user.password === password);

    if (user) {
      setIsLoggedIn(true);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
    setPassword("");
  };

  if (isLoggedIn) {
    return (
      <div>
        <p>Welcome, {username}!</p>
        <button onClick={handleLogout}>Logout</button>
        <Link to="/myaccount">
          <button>My Account</button>
        </Link>
      </div>
    );
  }

  return (
    <div>
      <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
}

function MyAccount() {
  // Replace this with the content you want to show on the user's account page
  return <p>This is your account page.</p>;
}

function App() {
  const [itemList, setItemList] = useState([]);
  const [userList, setUserList] = useState([]);

  useEffect(() => {
    // Fetch user data from localhost:3000/users
    fetch("http://localhost:3000/users")
      .then((response) => response.json())
      .then((data) => setUserList(data))
      .catch((error) => console.error("Error fetching user data:", error));
  }, []);

  const provided = {
    itemList,
    setItemList,
    userList,
    setUserList,
  };

  return (
    <AppContext.Provider value={provided}>
      <Router>
        <button onClick={() => navigate("/login")}>
          {isLoggedIn ? "My Account" : "Login"}
        </button>
        <Routes>
          <Route path="/" element={<Inventory />} />
          <Route path="item/:id" element={<ItemDetails />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/myaccount" element={<MyAccount />} />
        </Routes>
      </Router>
    </AppContext.Provider>
  );
}

export default App;
