import {useEffect,useState} from 'react';
import { Container, Row, Col } from 'react-bootstrap';

export default function Inventory() {
    const [itemList, setItemList] = useState();

    useEffect(() => {
          fetch('http://localhost:3001/items')
            .then(response => response.json())
            .then(data => {
             setItemList(data)
            })
            .catch(error => {
              console.error('Fetch error:', error);
            });        
        }, [])

        if (itemList) {
            console.log(itemList);
            let itemNames = [];
            let itemUserIds = [];
            for (let i=0; i < itemList.length; i++) {
                let currentItemName = itemList[i].item_name;
                itemNames.push(<li>{currentItemName}</li>);
                let currentItemUserId = itemList[i].user_id;
                itemUserIds.push(<li>{currentItemUserId}</li>)
            }

            return (
                <>
            <Container>
            <Col>
            <Row>
                {itemNames}
            </Row>
            </Col>
            </Container>
            <Container>
            <Col>
            <Row>
                {itemUserIds}
            </Row>
            </Col>
            </Container>
            </>
        )
    }   
}
