import { useState, useEffect, useContext } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useRoutes } from "react-router-dom";
import Inventory from "./components/Inventory";
import ItemDetails from "./components/ItemDetails";
import MyItems from "./components/MyItems";
import LoginPage from "./components/LoginPage";
import MyAccount from "./components/MyAccount";
import AppContext from "./AppContext.js";
import UserItemDetails from "./components/UserItemDetails";

function App() {
  const [itemList, setItemList] = useState([]);
  const [userList, setUserList] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState();

  const provided = {
    itemList,
    setItemList,
    userList,
    setUserList,
    isLoggedIn,
    setIsLoggedIn,
    currentUser,
    setCurrentUser,
  };

  // Define your routes using the useRoutes hook
  const routeResult = useRoutes([
    { path: "/", element: <Inventory /> },
    { path: "item/:id", element: <ItemDetails /> },
    { path: "/login", element: <LoginPage setIsLoggedIn={setIsLoggedIn} /> },
    { path: "/myaccount", element: <MyAccount setIsLoggedIn={setIsLoggedIn} /> },
    { path: "/user-item-details/:id", element: <UserItemDetails /> },
  ]);

  return (
    <AppContext.Provider value={provided}>
      <Router>
        <header>
          {isLoggedIn ? (
            // Check if the current route is not the "MyAccount" route before rendering the button
            !routeResult?.props?.path?.includes("/myaccount") && (
              <Link to="/myaccount">
                <button>My Account</button>
              </Link>
            )
          ) : (
            <Link to="/login">
              <button>Login</button>
            </Link>
          )}
        </header>
        {routeResult}
      </Router>
    </AppContext.Provider>
  );
}

export default App;
